package svg_test

import (
	"bytes"
	"fmt"

	g2d "github.com/jphsd/graphics2d"
	"github.com/jphsd/graphics2d/color"
	"github.com/jphsd/graphics2d/image"
	"github.com/jphsd/graphics2d/svg"
)

func ExampleImage() {
	s := g2d.NewShape(g2d.Circle([]float64{200, 200}, 100))
	img := image.NewRGBA(400, 400, color.White)
	pen := g2d.NewPen(color.Red, 3)
	g2d.DrawShape(img, s, pen)

	b := &bytes.Buffer{}
	enc := svg.NewEncoder(b)
	svg.Image(enc, img, "")
	svg.Complete(b)

	fmt.Println(b.String())
	// Output:
	// <svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink"><image width="400" height="400" xlink:href="data:image/png;base64,"></image></svg>
}
